import React, { useState } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red, green } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { withRouter } from "react-router-dom";
import Menus from './share'
import Header from './Header'
import data from './data';

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
    margin: 10,

  },
  media: {
    height: 0,
    paddingTop: '56.25%',
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(160deg)',
  },
  avatar: {
    backgroundColor: '#1f5aba',
  },
}));


 function OurCard(props) {
  const classes = useStyles();
  let [color, setColor] = useState()
  let [count, setCount] = useState(0)

  const handleExpandClick = () => {
    setColor(color = color === '' ? red[500] : '')
    setCount(count = count+1)
    return count
  };

  
  return (
  
    <Card className={classes.root}>
      <CardHeader
        avatar={
          <a href={props.value.name} style={{ color: 'white', textDecoration: 'none' }} >
            <Avatar aria-label="recipe" className={classes.avatar}>
              {props.value.name[0]}
            </Avatar>
          </a>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title={props.value.name}
        subheader="September 14, 2016"
      />
      <CardMedia
        className={classes.media}
        image={props.value.img}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {props.value.name}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
      {  props.history.location.pathname ===   '/'  ?  <a href={props.history.location.pathname}><IconButton onClick={handleExpandClick} aria-label="add to favorites">
      More
          <FavoriteIcon style={{ color }} /> 
        </IconButton></a> : <IconButton onClick={handleExpandClick} aria-label="add to favorites">
       gnel
          <FavoriteIcon style={{ color }} />
        </IconButton> }
      
        <Menus />
      </CardActions>
    </Card>
  );

}
export default withRouter(OurCard)
