import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import LongMenu from './Menu'
import Badge from '@material-ui/core/Badge';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import SearchIcon from '@material-ui/icons/Search';
import IconButton from '@material-ui/core/IconButton';
import Mercedes from './Mercedes'
import TextField from '@material-ui/core/TextField';
import OurCard from './Card'
import data from'./data'

const StyledBadge = withStyles((theme) => ({
  badge: {
    right: -3,
    top: 13,
    border: `2px solid ${theme.palette.background.paper}`,
    padding: '0 4px',
  },
}))(Badge);

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  rooot: {
    padding: '2px 4px',
    display: 'flex',
    alignItems: 'center',
    width: 400,
  },
  iconButton: {
    padding: 10,
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));
export default function Header() {
  let [value, setValue] = useState({})
  const onchange = (event) => {
    setValue({ value:  event ? event.target.value : '', initial: false})
    
  }
  const keyPress =(e)=>{
    if(e.keyCode == 13){
      setValue({ value: value.value , initial: true})
    }
 }
 

 
  const classes = useStyles();
  return (
  
    <div className={classes.root}>
    {value.initial ? <Mercedes /> :   <Grid container justify="center">
            {data.map((value) => (
              <Grid key={value} item>
                <OurCard ref={ref} className={classes.paper} value={value} iconCount={0} />
              </Grid>
            ))}
          </Grid> }
      <>
        <AppBar position="static">
          <Toolbar>
            <LongMenu />
            <Typography variant="h6" className={classes.title}>
              <a href='/' style={{ color: "white", textDecoration: 'none' }} >MyCar.am</a>
            </Typography>

            <div className={classes.rooot}>
              <IconButton type="submit" className={classes.iconButton} aria-label="search">
                <SearchIcon />
              </IconButton>
              <TextField id="standard-basic"
               label="Standard"
               onChange={onchange}
               value={value.value}
               onKeyDown={ keyPress}
                />
                </div> 
            <Button color="inherit">
              <StyledBadge badgeContent={0} color="secondary">
                <AddShoppingCartIcon />
              </StyledBadge></Button>
            <a href='/Login' style={{ color: 'white', textDecoration: 'none' }}><Button color="inherit">Login</Button></a>
          </Toolbar>
        </AppBar>
      </>
      )
    </div>
  );
}
