import React, { useEffect,useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import LongMenu from './Menu'
import Badge from '@material-ui/core/Badge';
import { withStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import SearchIcon from '@material-ui/icons/Search';
import IconButton from '@material-ui/core/IconButton';


const StyledBadge = withStyles((theme) => ({
  badge: {
    right: -3,
    top: 13,
    border: `2px solid ${theme.palette.background.paper}`,
    padding: '0 4px',
  },
}))(Badge);

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  rooot:{
    padding: '2px 4px',
    display: 'flex',
    alignItems: 'center',
    width: 400,
  },
  iconButton:{
    padding: 10,
  },
  input: {
    marginLeft: theme.spacing(1),
    flex: 1,
  },
  menuButton: {
    marginRight: theme.spacing(2),
  },
  title: {
    flexGrow: 1,
  },
}));
export default function Header() {
 
 const onchange =(event)=>{
  const [value,setValue]=useState({})
  setValue({value:event ? event.target.value :'',onchange:onchange})
  return{
    value,
    onchange
  }
 }
 useEffect(()=>{
  onchange()
 })

  const classes = useStyles();
  return (
    <div className={classes.root}>
      <AppBar position="static">
        <Toolbar>
          <LongMenu />
          <Typography variant="h6" className={classes.title}>
            <a href='/' style={{ color: "white", textDecoration: 'none' }} >MyCar.am</a>
          </Typography>

          <Paper component="form" className={classes.rooot}>
            <IconButton type="submit" className={classes.iconButton} aria-label="search">
              <SearchIcon />
            </IconButton>
            <InputBase
              className={classes.input}
              placeholder="Search "
              inputProps={{ 'aria-label': 'search google maps' }}
              {...value}
            />
          </Paper>

          <Button color="inherit">
            <StyledBadge badgeContent={0} color="secondary">
              <AddShoppingCartIcon />
            </StyledBadge></Button>
          <a href='/Login' style={{ color: 'white', textDecoration: 'none' }}><Button color="inherit">Login</Button></a>
        </Toolbar>
      </AppBar>
    </div>
  );
}
