import React, { useState } from 'react';
import { makeStyles, withTheme } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import CardMedia from '@material-ui/core/CardMedia';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Avatar from '@material-ui/core/Avatar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import { red } from '@material-ui/core/colors';
import FavoriteIcon from '@material-ui/icons/Favorite';
import ShareIcon from '@material-ui/icons/Share';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import Menus from './share'

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 345,
    margin: 10,

  },
  media: {
    height: 0,
    paddingTop: '56.25%',
  },
  expand: {
    transform: 'rotate(0deg)',
    marginLeft: 'auto',
    transition: theme.transitions.create('transform', {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: 'rotate(160deg)',
  },
  avatar: {
    backgroundColor: red[500],
  },
}));


export default function OurCard(props) {
  const classes = useStyles();
  let [color, setColor] = useState('')
  let [initial, setInitial] = useState(false)
  const handleExpandClick = () => {
    setColor(color = color === '' ? red[500] : '')
    let count = 0;
    count += 1;
    return count
  };

  


  return (

    <Card className={classes.root}>
      <CardHeader
        avatar={
          <a href={props.value.name} style={{ color: 'white', textDecoration: 'none' }} >
            <Avatar aria-label="recipe" className={classes.avatar}>
              {props.value.name[0]}
            </Avatar>
          </a>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title={props.value.name}
        subheader="September 14, 2016"
      />
      <CardMedia
        className={classes.media}
        image={props.value.img}
      />
      <CardContent>
        <Typography variant="body2" color="textSecondary" component="p">
          {props.value.name}
        </Typography>
      </CardContent>
      <CardActions disableSpacing>
       <IconButton onClick={handleExpandClick} aria-label="add to favorites">
          <FavoriteIcon style={{ color }} />
        </IconButton>
        <IconButton aria-label="share">
        <Menus />
        </IconButton>
      </CardActions>
    </Card>
  );

}
