import React from 'react';
import { useState, useEffect } from 'react';
import Avatar from '@material-ui/core/Avatar';
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Paper from '@material-ui/core/Paper';
import Box from '@material-ui/core/Box';
import Grid from '@material-ui/core/Grid';
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import Typography from '@material-ui/core/Typography';
import { withStyles } from '@material-ui/core/styles';
import fire from "../firebase.js";
import   "firebase/auth";
import firebase from'firebase'

function Copyright() {
    return (
        <Typography variant="body2" color="textSecondary" align="center">
            {'Copyright Â© '}
            <Link color="inherit" href='/'>
                MyCar.am
      </Link>{' '}
            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

const styles =  ((theme) => ({
    root: {
        height: '100vh',
    },
    image: {
        backgroundImage: 'url(https://cdn.wallpapersafari.com/20/21/WdDHMi.jpg)',
        backgroundRepeat: 'no-repeat',
        backgroundColor:
            theme.palette.type === 'light' ? theme.palette.grey[50] : theme.palette.grey[900],
        backgroundSize: 'cover',
        backgroundPosition: 'center',
    },
    paper: {
        margin: theme.spacing(8, 4),
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
    },
    avatar: {
        margin: theme.spacing(1),
        backgroundColor: theme.palette.secondary.main,
    },
    form: {
        width: '100%', // Fix IE 11 issue.
        marginTop: theme.spacing(1),
    },
    submit: {
        margin: theme.spacing(3, 0, 2),
    },
}));

function ForgotPass (props) {
    const [email,setEmail]=useState('')

   const  emailChange=(event)=>{
        
    setEmail(event.target.value)
    }
   const  changePassword=()=>{
         
        if(email) {
            firebase.auth().sendPasswordResetEmail(email).then(()=>alert('email has been sent to your email, please check and verify')).
            catch((error)=>alert(error.message))
        } else {
            alert('enter your email')
        }
    }
     
 
        const {classes}=props
         
        return (
        <Grid container component="main" className={classes.root}>
        <CssBaseline />
        <Grid item xs={false} sm={4} md={7} className={classes.image} />
        <Grid item xs={12} sm={8} md={5} component={Paper} elevation={6} square>
            <div className={classes.paper}>
                <Avatar className={classes.avatar}>
                    <LockOutlinedIcon />
                </Avatar>
                <Typography component="h1" variant="h5">
                    Restore Password
              </Typography>
                <form className={classes.form} noValidate>
                    <TextField
                        value={email}
                        onChange={emailChange}
                        variant="outlined"
                        margin="normal"
                        required
                        fullWidth
                        id="email"
                        label="Email Address or Phone Number"
                        name="email"
                        autoComplete="email"
                        autoFocus
                    />
                    <Link to="/">
                    <Button
                        onClick={changePassword}
                        type="submit"
                        fullWidth
                        variant="contained"
                        color="primary"
                        className={classes.submit}
                    >
                        Reset Password
                   </Button>
                   </Link>
                    <Grid container>
                        <Grid item>
                            <Link href="/SignUp" variant="body2">
                                {"Don't have an account? Sign Up"}
                            </Link>
                        </Grid>
                    </Grid>
                    <Box mt={5}>
                        <Copyright />
                    </Box>
                </form>
            </div>
        </Grid>
    </Grid>
);
    
  
 
}
export default withStyles(styles)(ForgotPass);
